/* 

  <if test={aluno.nota >= 7}>
      <span>...</span>
      <span>...</span>
      <span>...</span>
      <span>...</span>
  </if>

*/
export default props => {
  if(props.text) {
    return props.children
  } else {
    return false
  }
}
