import React, {Component}from "react";

class Contador extends Component {

  state = {
    numero: this.props.numeroInicial
  }

  render() {
    return (
      <div>
        <h2>Contador</h2>
        <p>{this.state.numero}</p>
       {/*  <p>Valor Inicial: {this.props.numeroInicial}</p> */}
      </div>
    )
  }
}
export default Contador
