import "./input.css"
import React, {useState} from "react";

const Infinput = (props) => {
  const [valor, setValor] = useState('Inicial')

  function quandoMudar(e) {
    setValor(e.tar)
    console.log(e.target.value)
  }

  return (
    <div className="Input">
      <input value={valor} onChange={quandoMudar} />
    </div>
  )
  
}
export	default Infinput
