import React from 'react';


export default (props) => {
  const usuario = props.usuario || {};
  return (
    <div>
      <If test={usuario && usuario.nome}>
        Seja BEM BINDO! <strong>{usuario.nome}</strong>!
      </If>
      <If test={usuario || usuario.nome}>
        Seja BEM BINDO! <strong>{usuario.nome}</strong>
      </If>
    </div>
  );
};
