import React, {useState} from 'react';

export default (props) => {
  function gerarNumeroContido(min, max, array) {
    const aleatorio = parseInt(Math.random() * (max + 1 - min)) + min;
    return array.includes(aleatorio)
      ? gerarNumeroContido(min, max, array)
      : aleatorio;
  }

  function gerarNumeros(qtde) {
    const numeros = Array(qtde)
      .fill(0)
      .reduce((nums) => {
        const novoNumero = gerarNumeroContido(1, 60, nums);
        return [...nums, novoNumero];
      }, [])
      .sort((n1, n2) => n1 - n2);
    return numeros;
  }

  const qtde = props.qut
  const numeroIniciais = Array(props.qtde || 6).fill(0)
  const [numeros, setNumeros] = useState (numeroIniciais) 

  return (
  <div>
    <h2>Mega</h2>
    <h3>{numeros.join(' ')}</h3>
    <button onClick={_ =>setNumeros(gerarNumeros())}>Gerar Números</button>
  </div>
  )
};
